mapper.update:
update alg_author_list
set aid = #{aid,jdbcType=VARCHAR},
  fullname = #{fullname,jdbcType=VARCHAR},
  simname = #{simname,jdbcType=VARCHAR},
  sid = #{sid,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, aid, fullname, simname, sid
from alg_author_list
where id = #{id,jdbcType=INTEGER}
