mapper.insert:
insert into alg_data_ver (data_ver_sn, data_sn, ver_sn
  )
values (#{dataVerSn,jdbcType=VARCHAR}, #{dataSn,jdbcType=VARCHAR}, #{verSn,jdbcType=VARCHAR}
  )

mapper.update:
update alg_data_ver
set data_sn = #{dataSn,jdbcType=VARCHAR},
  ver_sn = #{verSn,jdbcType=VARCHAR}
where data_ver_sn = #{dataVerSn,jdbcType=VARCHAR}

mapper.select:
select data_ver_sn, data_sn, ver_sn
from alg_data_ver
where data_ver_sn = #{dataVerSn,jdbcType=VARCHAR}
