mapper.select:
select dic_sn, dic_code, dic_key, dic_value, dic_sort, dic_desc, dic_parent_id, dic_relation
from alg_dic
where dic_sn = #{dicSn,jdbcType=VARCHAR}

select dic_sn, dic_code, dic_key, dic_value, dic_sort, dic_desc, dic_parent_id, dic_relation
from alg_dic
where dic_code = #{dicCode,jdbcType=VARCHAR}

select dic_sn, dic_code, dic_key, dic_value, dic_sort, dic_desc, dic_parent_id, dic_relation
from alg_dic
where dic_value = #{dicValue,jdbcType=VARCHAR}

select dic_value
from alg_dic
where dic_code = #{arg0,jdbcType=VARCHAR}
and dic_key = #{arg1,jdbcType=VARCHAR}
