mapper.insert:
insert into alg_model (model_sn, dat_sn, usr_sn, 
  model_set_sn, ver_sn, create_time, 
  model_address, model_name, model_en_name, 
  model_size, is_open_src, comment
  )
values (#{modelSn,jdbcType=VARCHAR}, #{datSn,jdbcType=VARCHAR}, #{usrSn,jdbcType=VARCHAR}, 
  #{modelSetSn,jdbcType=VARCHAR}, #{verSn,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{modelAddress,jdbcType=VARCHAR}, #{modelName,jdbcType=VARCHAR}, #{modelEnName,jdbcType=VARCHAR}, 
  #{modelSize,jdbcType=VARCHAR}, #{isOpenSrc,jdbcType=VARCHAR}, #{comment,jdbcType=VARCHAR}
  )

mapper.select:
select alg_model.model_sn, alg_model.dat_sn, alg_model.usr_sn, alg_model.model_set_sn, alg_model.ver_sn, date_format(alg_model.create_time,'%Y-%c-%d %h:%i:%s') create_time , alg_model.model_address,
alg_model.model_name, alg_model.model_en_name, alg_model.model_size, alg_model.is_open_src, alg_model.comment,alg_data.data_name
from alg_model left join  alg_data  on alg_data.data_sn =alg_model.dat_sn and alg_model.usr_sn =alg_data.usr_sn
where alg_model.model_set_sn = #{modelSetSn,jdbcType=VARCHAR}
and  alg_model.usr_sn=#{usrSn,jdbcType=VARCHAR}

select COUNT(1)
from alg_model
WHERE

  usr_sn = #{usrSn,jdbcType=VARCHAR}


  model_set_sn = #{modelSetSn,jdbcType=VARCHAR}

select count(*)
from alg_model
where usr_sn = #{usrSn,jdbcType=VARCHAR}
and model_name=#{modelName,jdbcType=VARCHAR}
and model_set_sn=#{modelSetSn,jdbcType=VARCHAR}

mapper.update:
update alg_model
set
creat_time = NOW(),
model_size = #{dataSize,jdbcType=VARCHAR},
model_address = #{dataAddr,jdbcType=VARCHAR}
where model_sn = #{dataSn,jdbcType=VARCHAR}

mapper.delete:
delete from alg_model
where model_sn = #{modelSn,jdbcType=VARCHAR}
      and usr_sn= #{usrSn,jdbcType=VARCHAR}

mapper.select:
