mapper.insert:
insert into alg_module_usage (usg_sn, mod_sn, star_cnt, 
  follow_cnt, call_cnt, credit_cnt
  )
values (#{usgSn,jdbcType=VARCHAR}, #{modSn,jdbcType=VARCHAR}, #{starCnt,jdbcType=BIGINT}, 
  #{followCnt,jdbcType=BIGINT}, #{callCnt,jdbcType=BIGINT}, #{creditCnt,jdbcType=DECIMAL}
  )

mapper.select:
select usg_sn, mod_sn, star_cnt, follow_cnt, call_cnt, credit_cnt
from alg_module_usage
where usg_sn = #{usgSn,jdbcType=VARCHAR}

select count(*)
from alg_module_usage
where mod_sn = #{modSn,jdbcType=VARCHAR}

select ifnull(star_cnt,0)
from alg_module_usage
where mod_sn = #{modSn,jdbcType=VARCHAR}
