mapper.insert:
insert into Account (AccSn, UsrSn, CashBal, 
  EarnBal, FreeBal, FreeSetTime
  )
values (#{accsn,jdbcType=VARCHAR}, #{usrsn,jdbcType=VARCHAR}, #{cashbal,jdbcType=DECIMAL}, 
  #{earnbal,jdbcType=DECIMAL}, #{freebal,jdbcType=DECIMAL}, #{freesettime,jdbcType=TIMESTAMP}
  )

mapper.select:
select AccSn, UsrSn, CashBal, EarnBal, FreeBal, FreeSetTime
from Account
where AccSn = #{accsn,jdbcType=VARCHAR}
