mapper.insert:
insert into alg_data_set (data_set_sn, usr_sn, data_set_name
  )
values (#{dataSetSn,jdbcType=VARCHAR}, #{usrSn,jdbcType=VARCHAR}, #{dataSetName,jdbcType=VARCHAR}
  )

mapper.update:
update alg_data_set
set usr_sn = #{usrSn,jdbcType=VARCHAR},
  data_set_name = #{dataSetName,jdbcType=VARCHAR}
where data_set_sn = #{dataSetSn,jdbcType=VARCHAR}

mapper.select:
select data_set_sn, usr_sn, data_set_name
from alg_data_set
where data_set_sn = #{dataSetSn,jdbcType=VARCHAR}
