mapper.insert:
insert into alg_license (lic_sn, lic_name, lic_content
  )
values (#{licSn,jdbcType=VARCHAR}, #{licName,jdbcType=VARCHAR}, #{licContent,jdbcType=LONGVARCHAR}
  )

mapper.update:
update alg_license
set lic_name = #{licName,jdbcType=VARCHAR},
  lic_content = #{licContent,jdbcType=LONGVARCHAR}
where lic_sn = #{licSn,jdbcType=VARCHAR}

mapper.select:
select lic_sn, lic_name, lic_content
from alg_license
where lic_sn = #{licSn,jdbcType=VARCHAR}

select lic_sn, lic_name, lic_content
from alg_license
where lic_name = #{licName,jdbcType=VARCHAR}

select lic_sn, lic_name, lic_content
from alg_license
where lic_sn = #{licSn,jdbcType=VARCHAR}
