mapper.update:
update alg_r_user_module_watch
set usr_sn = #{usrSn,jdbcType=VARCHAR},
  mod_sn = #{modSn,jdbcType=VARCHAR},
  wtc_has_star = #{wtcHasStar,jdbcType=VARCHAR},
  wtc_has_follow = #{wtcHasFollow,jdbcType=VARCHAR}
where wtc_sn = #{wtcSn,jdbcType=VARCHAR}

mapper.select:
select wtc_sn, usr_sn, mod_sn, wtc_has_star, wtc_has_follow
from alg_r_user_module_watch
where wtc_sn = #{wtcSn,jdbcType=VARCHAR}
