mapper.update:
update alg_star
set mod_sn = #{modSn,jdbcType=VARCHAR},
  usr_sn = #{usrSn,jdbcType=VARCHAR},
  creat_time = #{creatTime,jdbcType=TIMESTAMP}
where star_sn = #{starSn,jdbcType=VARCHAR}

mapper.select:
select star_sn, mod_sn, usr_sn, creat_time
from alg_star
where star_sn = #{starSn,jdbcType=VARCHAR}
