mapper.insert:
insert into License (LicSn, LicName, LicContent
  )
values (#{licsn,jdbcType=VARCHAR}, #{licname,jdbcType=VARCHAR}, #{liccontent,jdbcType=LONGVARCHAR}
  )

mapper.update:
update License
set LicName = #{licname,jdbcType=VARCHAR},
  LicContent = #{liccontent,jdbcType=LONGVARCHAR}
where LicSn = #{licsn,jdbcType=VARCHAR}

mapper.select:
select LicSn, LicName, LicContent
from License
where LicSn = #{licsn,jdbcType=VARCHAR}
