mapper.update:
update R_User_Module_Watch
set UsrSn = #{usrsn,jdbcType=VARCHAR},
  ModSn = #{modsn,jdbcType=VARCHAR},
  WtcHasStar = #{wtchasstar,jdbcType=VARCHAR},
  WtcHasFollow = #{wtchasfollow,jdbcType=VARCHAR}
where WtcSn = #{wtcsn,jdbcType=VARCHAR}

mapper.select:
select WtcSn, UsrSn, ModSn, WtcHasStar, WtcHasFollow
from R_User_Module_Watch
where WtcSn = #{wtcsn,jdbcType=VARCHAR}
