mapper.insert:
insert into SrcFile (SFLSn, SFLName, SFLUrl
  )
values (#{sflsn,jdbcType=VARCHAR}, #{sflname,jdbcType=VARCHAR}, #{sflurl,jdbcType=VARCHAR}
  )

mapper.update:
update SrcFile
set SFLName = #{sflname,jdbcType=VARCHAR},
  SFLUrl = #{sflurl,jdbcType=VARCHAR}
where SFLSn = #{sflsn,jdbcType=VARCHAR}

mapper.select:
select SFLSn, SFLName, SFLUrl
from SrcFile
where SFLSn = #{sflsn,jdbcType=VARCHAR}
