mapper.insert:
insert into VersionUsage (UsgSn, ModSn, StarCnt, 
  FollowCnt, CallCnt, CreditCnt
  )
values (#{usgsn,jdbcType=VARCHAR}, #{modsn,jdbcType=VARCHAR}, #{starcnt,jdbcType=INTEGER}, 
  #{followcnt,jdbcType=INTEGER}, #{callcnt,jdbcType=INTEGER}, #{creditcnt,jdbcType=DECIMAL}
  )

mapper.select:
select UsgSn, ModSn, StarCnt, FollowCnt, CallCnt, CreditCnt
from VersionUsage
where UsgSn = #{usgsn,jdbcType=VARCHAR}
