mapper.update:
update al_activity
set activity_name = #{activityName,jdbcType=VARCHAR},
  activity_rule = #{activityRule,jdbcType=INTEGER},
  activity_statue = #{activityStatue,jdbcType=CHAR},
  create_date = #{createDate,jdbcType=BIGINT}
where activity_id = #{activityId,jdbcType=INTEGER}

mapper.select:
select activity_id, activity_name, activity_rule, activity_statue, create_date
from al_activity
where activity_id = #{activityId,jdbcType=INTEGER}
