mapper.insert:
insert into SYNONYM (SYNID, CODEID, SYNNAME
  )
values (#{synid,jdbcType=VARCHAR}, #{codeid,jdbcType=VARCHAR}, #{synname,jdbcType=VARCHAR}
  )

mapper.update:
update SYNONYM
set CODEID = #{codeid,jdbcType=VARCHAR},
  SYNNAME = #{synname,jdbcType=VARCHAR}
where SYNID = #{synid,jdbcType=VARCHAR}

mapper.select:
select SYNID, CODEID, SYNNAME
from SYNONYM
where SYNID = #{synid,jdbcType=VARCHAR}
